import React, { useState } from "react";
import { AppBar, Toolbar, Typography, IconButton, Card, CardMedia, CardContent, Dialog, DialogActions, DialogContent, DialogTitle, Button, Switch } from "@mui/material";
import { Delete, Edit, Menu } from "@mui/icons-material";
import "./styles.css";

const images = [
  { id: 1, src: "https://source.unsplash.com/random/1", title: "Image 1" },
  { id: 2, src: "https://source.unsplash.com/random/2", title: "Image 2" },
  { id: 3, src: "https://source.unsplash.com/random/3", title: "Image 3" }
];

export default function ModernUI() {
  const [darkMode, setDarkMode] = useState(false);
  const [open, setOpen] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  const handleClickOpen = (image) => {
    setSelectedImage(image);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setSelectedImage(null);
  };

  return (
    <div className={darkMode ? "dark-mode" : "light-mode"}>
      <AppBar position="static" className="navbar">
        <Toolbar>
          <IconButton edge="start" color="inherit">
            <Menu />
          </IconButton>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Modern UI
          </Typography>
          <Switch checked={darkMode} onChange={() => setDarkMode(!darkMode)} />
        </Toolbar>
      </AppBar>
      <div className="image-grid">
        {images.map((image) => (
          <Card key={image.id} className="image-card">
            <CardMedia
              component="img"
              height="200"
              image={image.src}
              alt={image.title}
              onClick={() => handleClickOpen(image)}
            />
            <CardContent>
              <Typography>{image.title}</Typography>
              <IconButton color="primary" onClick={() => handleClickOpen(image)}>
                <Edit />
              </IconButton>
              <IconButton color="error">
                <Delete />
              </IconButton>
            </CardContent>
          </Card>
        ))}
      </div>

      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Edit Image</DialogTitle>
        <DialogContent>
          <Typography>{selectedImage?.title}</Typography>
          <img src={selectedImage?.src} alt={selectedImage?.title} style={{ width: "100%", borderRadius: "10px" }} />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
